		public static string NormalizeSpace(string a)
		{
			string result = "";
			bool needSpace = false;
			for (int i=0; i < a.Length; i++)
			{
				char c = a[i];
				if (c ==' ' || c == '\t' || c == '\n' || c == '\r')
					needSpace = true;
				else
				{
					if (needSpace && result.Length != 0)
						result += ' ';
					result += c;
					needSpace = false;
				}
			}
			return result;
		}

