		public static string Translate(string a, string b, string c)
		{
			string result = "";
			for (int i=0; i< a.Length; i++)
			{
				char ai = a[i];
				int off = b.IndexOf(ai);
				if (off != -1 && off < c.Length)
					result += c[off];
				else if (off == -1)
					result += ai;
			}
			return result;
		}

