								if ((fieldInfo.mComponent != 'z' && fieldInfo.mComponent != 'Z') || dt.HasTimezone)
									sValue = processFormatModifier(dt, sValue, fieldInfo);
								sValue = processWidth(sValue, fieldInfo);

