	{
		NORMAL,
		INFIELD
	}

	protected enum ArgumentState
	{
		COMPONENT,
		FORMAT,
		WIDTH
	}

	private readonly string[] DayNames = { 
		"Sunday",
		"Monday",
		"Tuesday",
		"Wednesday",
